INSERT INTO questions (question, answers, correct_answer, category) VALUES
  ('A chi è rivolto il servizio di noleggio con conducente?', ARRAY['ad una utenza indifferenziata', 'all''utenza specifica che si deve rivolgere presso la sede del vettore o mediante mezzi tecnologici', 'all''utenza specifica che prenota presso le apposite aree pubbliche di stazionamento'], 'all''utenza specifica che si deve rivolgere presso la sede del vettore o mediante mezzi tecnologici', 'Legislazione Siciliana'),
  ('A chi può essere rilasciata la licenza taxi?', ARRAY['a persone singole', 'a persone singole o a cooperative', 'a qualsiasi impresa, individuale o societaria'], 'a persone singole', 'Legislazione Siciliana'),
  ('A cosa serve l''iscrizione al ruolo dei conducenti dei veicoli adibiti a servizi pubblici non di linea?', ARRAY['a ottenere le licenze e le autorizzazioni', 'a svolgere l''attività di conducente dei rispettivi mezzi ed a ottenere le licenze e le autorizzazioni', 'a condurre i mezzi in servizio pubblico non di linea'], 'a svolgere l''attività di conducente dei rispettivi mezzi ed a ottenere le licenze e le autorizzazioni', 'Legislazione Siciliana'),
  ('A cosa serve l''iscrizione al ruolo dei conducenti di veicoli adibiti ad autoservizi pubblici non di linea?', ARRAY['a garantire la serietà e la professionalità', 'ad acquisire un requisito necessario per ottenere le licenze e le autorizzazioni necessarie all''esercizio dei servizi', 'ad evitare controlli e verifiche da parte dei Comuni'], 'ad acquisire un requisito necessario per ottenere le licenze e le autorizzazioni necessarie all''esercizio dei servizi', 'Legislazione Siciliana'),
  ('A cosa si estende la responsabilità del vettore nel contratto di trasporto, se avviene con ritardo o non viene effettuato per motivi imputabili al trasportatore stesso?', ARRAY['solo ad eventuali perdite', 'anche a mancati guadagni causati dal mancato adempimento contrattuale', 'solo al prezzo già pagato per il trasporto, che deve essere rimborsato'], 'anche a mancati guadagni causati dal mancato adempimento contrattuale', 'Legislazione Siciliana'),
  ('A favore di chi deve essere effettuato il servizio di taxi?', ARRAY['del trasportato', 'di chiunque intenda far prelevare e trasportare altre persone', 'del trasportato anche con prenotazione per servizi periodici o continuativi'], 'del trasportato', 'Legislazione Siciliana'),
  ('A quale categoria di veicoli appartengono i veicoli d''epoca o quelli di interesse storico o collezionistico?', ARRAY['a quella corrispondente alle caratteristiche tecniche previste dal Codice della Strada', 'a quella dei veicoli con caratteristiche atipiche', 'a quella dei veicoli cancellati dal PRA'], 'a quella dei veicoli con caratteristiche atipiche', 'Legislazione Siciliana'),
  ('A quale delle seguenti attività si applica l''esenzione dall''obbligo generalizzato di emissioni di ricevuta o scontrino fiscale per le prestazioni di trasporto effettuate?', ARRAY['il trasporto con veicoli a trazione animale e servizio di taxi', 'servizio di taxi e noleggio con conducente con autovettura', 'servizio di noleggio con autovettura'], 'il trasporto con veicoli a trazione animale e servizio di taxi', 'Legislazione Siciliana'),
  ('A quale Ente sono state trasferite dallo Stato le proprie competenze in materia di trasporto pubblico?', ARRAY['alla Regione, in quanto di competenza per l''assetto e l''utilizzazione del territorio', 'al Comune, in quanto di competenza per la disciplina delle relative attività commerciali', 'alla Provincia, in quanto di competenza per la tutela dell''utenza'], 'alla Regione, in quanto di competenza per l''assetto e l''utilizzazione del territorio', 'Legislazione Siciliana'),
  ('A quale fine deve essere ricercata l''uniformazione delle regolamentazioni comunali all''interno delle aree comprensoriali?', ARRAY['per evitare concorrenza indebite tra tassisti e noleggiatori di comuni limitrofi', 'per ottenere una maggiore razionalità ed efficienza del servizio pubblico non di linea', 'per limitare il numero delle licenze e delle autorizzazioni al minimo possibile'], 'per ottenere una maggiore razionalità ed efficienza del servizio pubblico non di linea', 'Legislazione Siciliana'),
  ('A quali condizioni è consentito trasportare animali domestici nelle autovetture?', ARRAY['non è consentito in alcun caso', 'al massimo uno', 'senza limiti, purché siano custoditi in apposita gabbia o contenitore o nel vano posteriore al posto di guida, con divisorio'], 'senza limiti, purché siano custoditi in apposita gabbia o contenitore o nel vano posteriore al posto di guida, con divisorio', 'Legislazione Siciliana'),
  ('A quanti veicoli addetti ad autoservizi pubblici non di linea può fare riferimento una singola licenza o autorizzazione?', ARRAY['a un solo taxi, a più veicoli per noleggio con conducente', 'a un solo taxi o veicolo per noleggio con conducente', 'a più taxi o veicoli per noleggio con conducente'], 'a un solo taxi o veicolo per noleggio con conducente', 'Legislazione Siciliana'),
  ('Se alla domanda per avere una licenza o autorizzazione non viene emesso un formale atto di diniego, l''ente pubblico può intervenire successivamente?', ARRAY['no, la licenza o l''autorizzazione si intendono acquisite definitivamente', 'si, l''atto viene dichiarato nullo e l''esercente può essere sanzionato per esercizio abusivo', 'si, l''atto può essere annullato per illegittimità per ragioni di pubblico interesse'], 'si, l''atto può essere annullato per illegittimità per ragioni di pubblico interesse', 'Legislazione Siciliana'),
  ('Alla guida di un autoveicolo adibito a servizio di taxi o noleggio con conducente l''uso della cintura di sicurezza e'' obbligatorio:', ARRAY['mai', 'sempre', 'solo fuori dai centri abitati'], 'sempre', 'Legislazione Siciliana'),
  ('Che dimensione massima di scritte e stemmi dell''azienda esercente il servizio di taxi e il Comune di appartenenza è ammessa sulle fiancate della vettura?', ARRAY['non possono essere ammesse', 'quelle identificate dal regolamento comunale', '875 cmq'], '875 cmq', 'Legislazione Siciliana'),
  ('Che sanzione è prevista per scritte pubblicitarie luminose poste su veicoli adibiti a servizi pubblici non di linea ma difformi da quanto eventualmente possibile?', ARRAY['una sanzione amministrativa', 'una multa', 'il sequestro del veicolo'], 'una sanzione amministrativa', 'Legislazione Siciliana'),
  ('Che sanzione subisce chi guida veicoli adibiti a servizi non di linea che circola senza avere con sé licenza o autorizzazione comunale regolarmente rilasciata?', ARRAY['nessuna sanzione', 'una sanzione amministrativa', 'la sospensione della licenza o autorizzazione per sette giorni'], 'una sanzione amministrativa', 'Legislazione Siciliana'),
  ('Che sanzione subisce chi, pur avendo l''autorizzazione, guida una vettura adibita al servizio di n.c.c. senza osservare le norme o le condizioni dell''autorizzazione?', ARRAY['un''ammenda', 'una sanzione amministrativa e sospensione della carta di circolazione da due a otto mesi', 'le sanzioni previste dai regolamenti comunali di esercizio'], 'una sanzione amministrativa e sospensione della carta di circolazione da due a otto mesi', 'Legislazione Siciliana'),
  ('Quale targa particolare devono esporre le autovetture adibite al servizio di noleggio con conducente?', ARRAY['una posteriore, inamovibile, con dicitura “NCC”, numero progressivo e stemma comunale', 'una anteriore, con dicitura “NCC”', 'nessuna targa oltre a quella di immatricolazione'], 'una posteriore, inamovibile, con dicitura “NCC”, numero progressivo e stemma comunale', 'Legislazione Siciliana'),
  ('Chi determina le tariffe del servizio di taxi?', ARRAY['la libera contrattazione tra il cliente e il vettore, entro limiti minimi e massimi comunali', 'i Comuni che rilasciano le licenze', 'la Camera di Commercio'], 'i Comuni che rilasciano le licenze', 'Legislazione Siciliana'),
  ('Chi può adottare norme speciali per assicurare il coordinamento e l''uniformità dei servizi pubblici non di linea, entro le zone ad intensa agglomerazione urbana?', ARRAY['la Regione', 'la Provincia', 'I Comuni interessati'], 'I Comuni interessati', 'Legislazione Siciliana'),
  ('Chi può prevedere, per particolari casi o esigenze, speciali prescrizioni per tipo e caratteristiche dei veicoli adibiti a servizi di taxi o noleggio con conducente?', ARRAY['la Provincia', 'l''Ufficio Periferico del Dipartimento dei Trasporti Terrestri – ex MCTC', 'il Comune'], 'il Comune', 'Legislazione Siciliana'),
  ('Come deve essere organizzato il parcheggio per autoveicoli adibiti al trasporto di portatori di handicap, con posti disposti parallelamente al senso di marcia?', ARRAY['i posti non possono essere disposti paralleli al senso di marcia', 'deve essere consentito il passaggio di una sedia a rotelle tra due veicoli', 'basta che sia segnalato'], 'deve essere consentito il passaggio di una sedia a rotelle tra due veicoli', 'Legislazione Siciliana'),
  ('Come possono essere effettuate le prestazioni di noleggio di veicoli con conducente?', ARRAY['a tempo e/o a viaggio', 'solo a tempo', 'solo a viaggio'], 'a tempo e/o a viaggio', 'Legislazione Siciliana'),
  ('Come può essere regolato il rapporto tra un tassista e il suo sostituto?', ARRAY['da un contratto di lavoro a tempo determinato o da un contratto di gestione al massimo di sei mesi', 'da un contratto di prestazione d''opera', 'anche informalmente'], 'da un contratto di lavoro a tempo determinato o da un contratto di gestione al massimo di sei mesi', 'Legislazione Siciliana'),
  ('Come si determina la tariffa del servizio taxi?', ARRAY['a tempo', 'a distanza', 'a base multipla (tempo e distanzin servizio urbano, a distanza in servizio extraurbano'], 'a base multipla (tempo e distanzin servizio urbano, a distanza in servizio extraurbano', 'Legislazione Siciliana'),
  ('Come sono definiti gli autoservizi pubblici non di linea?', ARRAY['servizi con funzione complementare e integrativa a quelli di linea con qualsiasi mezzo effettuati', 'servizi svolti senza obbligo di aderire alla richiesta di trasporto', 'servizi ad offerta indifferenziata'], 'servizi con funzione complementare e integrativa a quelli di linea con qualsiasi mezzo effettuati', 'Legislazione Siciliana'),
  ('Come vengono contraddistinte le autovetture adibite ai servizi di taxi?', ARRAY['da un numero d''ordine assegnato dal Comune', 'da un nome in codice seguito da un numero', 'da un simbolo diverso per ciascuna vettura'], 'da un numero d''ordine assegnato dal Comune', 'Legislazione Siciliana'),
  ('Come viene definita la locazione di un veicolo senza conducente?', ARRAY['veicolo senza conducente? l''obbligo da parte del locatore di metterlo a disposizione del locatario a pagamento', 'pagamento il prestito gratuito di un''autovettura adibita ad uso privato', 'un contratto in leasing'], 'veicolo senza conducente? l''obbligo da parte del locatore di metterlo a disposizione del locatario a pagamento', 'Legislazione Siciliana'),
  ('Come viene definito l''uso di in veicolo adibito a servizio di noleggio con conducente?', ARRAY['uso privato', 'uso di terzi', 'uso pubblico'], 'uso di terzi', 'Legislazione Siciliana'),
  ('Come viene definito l''uso di un veicolo adibito a servizio di piazza o taxi?', ARRAY['uso privato', 'uso di terzi', 'uso pubblico'], 'uso di terzi', 'Legislazione Siciliana'),
  ('Come viene rilasciata la licenza o l''autorizzazione ad autoservizi pubblici non di linea?', ARRAY['a richiesta', 'a seguito di denuncia di inizio del servizio', 'tramite concorso pubblico'], 'tramite concorso pubblico', 'Legislazione Siciliana'),
  ('Con quale atto pubblico il Comune individua le aree da destinare allo stazionamento dei veicoli per i servizi pubblici non di linea?', ARRAY['col regolamento di esercizio', 'con ordinanze del Sindaco', 'con decreto del responsabile dell''ufficio comunale per il commercio'], 'con ordinanze del Sindaco', 'Legislazione Siciliana'),
  ('Con quale obiettivo le Regioni delegano agli enti locali l''esercizio delle funzioni amministrative in materia di autoservizi pubblici non di linea?', ARRAY['per la semplificazione delle procedure', 'per il decentramento amministrativo', 'per garantire la visione integrata del trasporto non di linea con gli altri modi di trasporto'], 'per garantire la visione integrata del trasporto non di linea con gli altri modi di trasporto', 'Legislazione Siciliana'),
  ('Cosa caratterizza le autovetture adibite al servizio di taxi rispetto a quelle adibite al servizio di noleggio con conducente?', ARRAY['l''obbligo di essere munite di tassametro omologato', 'l''obbligo di essere attrezzate con dispositivi antinquinamento', 'l''obbligo di avere quattro porte'], 'l''obbligo di essere munite di tassametro omologato', 'Legislazione Siciliana'),
  ('Quali delle seguenti autovetture in servizio di trasporto pubblico non di linea non sono obbligate all''adozione del colore uniforme?', ARRAY['linea non sono obbligate all''adozione del colore uniforme? immatricolate per servizio di taxi sulla base di licenza comunale', 'immatricolate per servizio di noleggio con conducente e autorizzate a effettuare servizio di taxi', 'immatricolate per servizio di noleggio con conducente.'], 'immatricolate per servizio di noleggio con conducente.', 'Legislazione Siciliana'),
  ('Cosa differenzia il contratto di noleggio rispetto al contratto di trasporto di cose ?', ARRAY['il trasporto è disciplinato dal Codice Civile, per il noleggio si fa riferimento al Decreto Ministeriale del 20/04/1993', 'il trasporto è servizio pubblico, il noleggio no', 'il trasporto è obbligatorio, il noleggio no'], 'il trasporto è disciplinato dal Codice Civile, per il noleggio si fa riferimento al Decreto Ministeriale del 20/04/1993', 'Legislazione Siciliana'),
  ('Quali competenze ha assunto la Regione relativamente ai servizi pubblici non di linea?', ARRAY['approvazione dei regolamenti comunali relativi ai noleggi e ai servizi di piazza', 'predisposizione di criteri per la redazione di regolamenti comunali dei servizi di noleggio e piazza', 'supervisione dell''esercizio dei trasporti pubblici non di linea'], 'supervisione dell''esercizio dei trasporti pubblici non di linea', 'Legislazione Siciliana'),
  ('Di quale colore devono essere le autovetture adibite al servizio di taxi, immatricolate dal 01.01.1993?', ARRAY['bianco', 'giallo', 'quello determinato dal regolamento comunale'], 'bianco', 'Legislazione Siciliana'),
  ('Dove avviene lo stazionamento dei mezzi addetti al servizio di noleggio con conducente?', ARRAY['presso apposite autorimesse pubbliche', 'su suolo pubblico in aree specificatamente contrassegnate', 'su suolo privato, in apposite rimesse'], 'su suolo privato, in apposite rimesse', 'Legislazione Siciliana'),
  ('Dove deve avvenire il prelevamento dell''utente o l''inizio del servizio di taxi?', ARRAY['esclusivamente entro l''area comunale', 'entro l''area comunale o comprensoriale', 'entro l''area provinciale'], 'entro l''area comunale o comprensoriale', 'Legislazione Siciliana'),
  ('Dove è obbligatoria la prestazione del servizio di taxi?', ARRAY['per qualsiasi destinazione, anche esterna al limite dell''area in cui è consentito acquisire utenza', 'entro l''area provinciale', 'entro l''area comunale o comprensoriale'], 'entro l''area comunale o comprensoriale', 'Legislazione Siciliana'),
  ('Dove può avvenire lo stazionamento dei veicoli adibiti a servizio di taxi, durante il servizio stesso?', ARRAY['esclusivamente su suolo pubblico, in aree specificatamente individuate', 'esclusivamente su piazze', 'indifferentemente su suolo pubblico o presso apposite rimesse private'], 'esclusivamente su suolo pubblico, in aree specificatamente individuate', 'Legislazione Siciliana'),
  ('Dove può essere prelevato l''utente che ha prenotato un servizio di noleggio con conducente?', ARRAY['solo presso la rimessa o sede del vettore', 'in qualsiasi punto, purché interno al territorio comunale', 'in qualsiasi punto esterno alla sede o rimessa, purché la prenotazione sia disponibile nel veicolo'], 'in qualsiasi punto esterno alla sede o rimessa, purché la prenotazione sia disponibile nel veicolo', 'Legislazione Siciliana'),
  ('Dove vengono effettuate le prenotazioni di trasporto nel servizio di noleggio con conducente con autovettura?', ARRAY['presso la sede del vettore', 'presso la rimessa o tramite mezzi tecnologici', 'presso appositi spazi pubblici'], 'presso la rimessa o tramite mezzi tecnologici', 'Legislazione Siciliana'),
  ('E'' ammessa la pubblicità non luminosa a pagamento per conto di terzi sulle autovetture adibite a servizio taxi e noleggio con conducente?', ARRAY['si', 'solo sui taxi', 'solo sulle auto da noleggio'], 'si', 'Legislazione Siciliana'),
  ('E'' ammessa la pubblicità non luminosa sui veicoli addetti a servizi pubblici non di linea?', ARRAY['solo sui taxi', 'solo sulle autovetture adibite al noleggio con conducente', 'si, solo a determinate condizioni'], 'si, solo a determinate condizioni', 'Legislazione Siciliana'),
  ('E'' ammessa la pubblicità non luminosa sui veicoli adibiti a servizi non di linea, mediante l''applicazione su tutte le superfici non vetrate di pellicole autoadesive?', ARRAY['si, senza limite', 'si, solo sui taxi', 'no'], 'si, solo sui taxi', 'Legislazione Siciliana'),
  ('E'' consentita la sosta di altri veicoli nelle apposite piazzole destinate al servizio taxi?', ARRAY['si, sempre', 'no, e nel caso si applicano una sanzione pecuniaria e la decurtazione di due punti dalla patente', 'solo se esiste una specifica indicazione con cartello'], 'no, e nel caso si applicano una sanzione pecuniaria e la decurtazione di due punti dalla patente', 'Legislazione Siciliana'),
  ('Quando i tassisti hanno l''obbligo dell''uso della cintura di sicurezza?', ARRAY['mai', 'solo in servizio nei centri abitati', 'sempre'], 'sempre', 'Legislazione Siciliana'),
  ('E'' possibile cumulare più licenze di taxi in capo al medesimo soggetto?', ARRAY['no, perché l''esercizio deve essere personale', 'si, se esso è associato a una cooperativa', 'si, se esso è titolare di impresa artigiana con sufficiente numero di dipendenti'], 'no, perché l''esercizio deve essere personale', 'Legislazione Siciliana'),
  ('E'' possibile installare su veicoli adibiti a servizi pubblici non di linea scritte o insegne pubblicitarie luminose?', ARRAY['si nei Taxi', 'no', 'si, solo nei casi previsti dal regolamento comunale di esercizio'], 'si nei Taxi', 'Legislazione Siciliana'),
  ('E'' possibile rientrare in possesso di licenze o autorizzazioni per autoservizi pubblici non di linea conferite a organismi cooperativi?', ARRAY['no', 'si, in caso di recesso, decadenza o esclusione', 'si, in qualsiasi caso'], 'si, in caso di recesso, decadenza o esclusione', 'Legislazione Siciliana'),
  ('E'' possibile utilizzare per uso proprio un''autovettura adibita a servizio di taxi?', ARRAY['no, mai', 'si, con specifico nulla osta del Sindaco', 'si, fuori servizio'], 'si, fuori servizio', 'Legislazione Siciliana'),
  ('E'' prevista la partecipazione delle associazioni degli utenti alle commissioni consultive per gli autoservizi pubblici non di linea?', ARRAY['solo se ammesso dai Comuni e dalle Regioni', 'sempre, per obbligo di legge', 'no'], 'sempre, per obbligo di legge', 'Legislazione Siciliana'),
  ('E'' sempre sufficiente il tassametro omologato per indicare la tariffa da pagare nel servizio di taxi?', ARRAY['no, se esistono eventuali supplementi questi devono essere indicati in apposito avviso sul cruscotto', 'si', 'no, è necessario fare un preventivo al momento della partenza'], 'no, se esistono eventuali supplementi questi devono essere indicati in apposito avviso sul cruscotto', 'Legislazione Siciliana'),
  ('Esistono casi particolari di esclusione dall''esenzione da IVA di prestazioni di servizi di trasporto pubblico non di linea che di norma sono esenti?', ARRAY['quando il trasporto avviene per un contratto stipulato con un soggetto diverso dal viaggiatore', 'No', 'quando il trasporto avviene per un contratto di noleggio a tempo'], 'No', 'Legislazione Siciliana'),
  ('Gli autoservizi pubblici non di linea sono disciplinati solo da regolamenti di esercizio predisposti dai Comuni?', ARRAY['no, possono essere emanate anche norme speciali dalla Regione o ente delegato per comprensorio', 'si', 'no, sono regolati anche dalle disposizioni delle Camere di Commercio'], 'no, possono essere emanate anche norme speciali dalla Regione o ente delegato per comprensorio', 'Legislazione Siciliana'),
  ('I Comuni e le Province sono titolari di funzioni proprie in materia di regolamentazione del trasporto pubblico non di linea?', ARRAY['si', 'no, sono funzioni delegate dalla Regione e devono sottostare al rispetto delle norme regionali', 'si, ma devono coordinare tra loro per evitare disomogeneità normative'], 'no, sono funzioni delegate dalla Regione e devono sottostare al rispetto delle norme regionali', 'Legislazione Siciliana'),
  ('I servizi di trasporto pubblico non di linea possono presentarsi in forme concorrenziali al trasporto pubblico di linea?', ARRAY['si, non esistono divieti', 'no, la loro funzione è definita dalla legge come integrativa e complementare ai servizi di linea', 'si, ma solo a condizione che l''esercente dei servizi di linea non faccia opposizione'], 'no, la loro funzione è definita dalla legge come integrativa e complementare ai servizi di linea', 'Legislazione Siciliana'),
  ('I servizi di trasporto pubblico non di linea sono assoggettati ad IVA?', ARRAY['no, sono esenti', 'si, in ogni caso', 'solo quando sono svolti con veicoli da piazza tra comuni distanti tra loro 50 Km'], 'solo quando sono svolti con veicoli da piazza tra comuni distanti tra loro 50 Km', 'Legislazione Siciliana'),
  ('titolari di licenza o autorizzazione per autoservizi pubblici non di linea possono avvalersi di collaboratori familiari?', ARRAY['si, purché iscritti a ruolo', 'si, senza particolari obblighi', 'no, solo da sostituti o dipendenti'], 'si, purché iscritti a ruolo', 'Legislazione Siciliana'),
  ('titolari di licenza taxi possono farsi sostituire alla guida del taxi?', ARRAY['si, sia temporaneamente che definitivamente', 'solo temporaneamente e per specifici motivi', 'solo per incarichi sindacali'], 'solo temporaneamente e per specifici motivi', 'Legislazione Siciliana'),
  ('I titolari di licenze o autorizzazioni per servizi pubblici non di linea possono costituire forme associative tra di loro e con altri soggetti?', ARRAY['possono associarsi in cooperative, consorzi di imprese artigiane ed altro, ma solo tra titolari', 'possono associarsi liberamente con chiunque in tutte le forme previste dal codice civile', 'no, le società possono svolgere i servizi pubblici solo se sono direttamente titolari'], 'possono associarsi in cooperative, consorzi di imprese artigiane ed altro, ma solo tra titolari', 'Legislazione Siciliana'),
  ('Il Comune può sottoporre a particolare verifica un veicolo da adibire a servizio taxi o noleggio con conducente, prima che sia immesso in servizio?', ARRAY['no, deve solo accertare l''avvenuto rilascio della specifica carta di circolazione', 'no, può solo segnalare all''Ufficio Periferico del DDT – ex MCTC – le sue necessità all''atto del rilascio della carta di circolazione', 'si, anzi deve verificarne la rispondenza ad eventuali prescrizioni specifiche'], 'si, anzi deve verificarne la rispondenza ad eventuali prescrizioni specifiche', 'Legislazione Siciliana'),
  ('Il contratto di lavoro a tempo determinato per la sostituzione di un tassista può essere prorogato senza effetti particolari sul rapporto di lavoro?', ARRAY['si', 'in alcuni casi specifici', 'no'], 'si', 'Legislazione Siciliana'),
  ('Il servizio di autonoleggio con conducente ha obblighi particolari in relazione all''inizio del servizio?', ARRAY['no', 'si, l''inizio del servizio deve avvenire con partenza dalle rimesse poste nell''intero territorio regionale', 'si, l''inizio del servizio deve avvenire con partenza all''interno del territorio comunale'], 'si, l''inizio del servizio deve avvenire con partenza dalle rimesse poste nell''intero territorio regionale', 'Legislazione Siciliana'),
  ('Il servizio di linea per trasporto di persone può essere effettuato con autovetture?', ARRAY['si', 'no', 'solo se attrezzate con dispositivi particolari'], 'si', 'Legislazione Siciliana'),
  ('Il servizio di noleggio con conducente è soggetto a tariffa predeterminata?', ARRAY['si, con tariffa unica chilometrica obbligatoria', 'no, il corrispettivo è contrattabile senza limiti', 'si, con tariffe chilometriche minime e massime entro cui si può contrattare il corrispettivo'], 'si, con tariffe chilometriche minime e massime entro cui si può contrattare il corrispettivo', 'Legislazione Siciliana'),
  ('Il servizio taxi ha il solo scopo di soddisfare le richieste di trasporto individuali?', ARRAY['si, se il trasporto viene richiesto da più persone si tratta di noleggio', 'no, il trasporto può essere richiesto anche da piccoli gruppi', 'si, è vietato imbarcare persone con la diversa destinazione'], 'no, il trasporto può essere richiesto anche da piccoli gruppi', 'Legislazione Siciliana'),
  ('Il servizio taxi ha obblighi particolari relativi al prelevamento dell''utente o all''inizio del servizio?', ARRAY['si, essi devono avvenire con partenza dal territorio del Comune che ha rilasciato la licenza', 'no', 'si, essi devono avvenire unicamente entro il territorio del Comune che ha rilasciato la licenza'], 'si, essi devono avvenire con partenza dal territorio del Comune che ha rilasciato la licenza', 'Legislazione Siciliana'),
  ('In caso di acquisto in leasing di un veicolo a motore da adibire a servizi pubblici non di linea, a nome di chi esso viene immatricolato?', ARRAY['del locatore', 'del locatario', 'del locatore, ma si trascrive sulla carta di circolazione il nome del locatario'], 'del locatore, ma si trascrive sulla carta di circolazione il nome del locatario', 'Legislazione Siciliana'),
  ('In caso di acquisto in leasing di un veicolo a motore da adibire a servizi pubblici non di linea, per quale uso viene immatricolato il veicolo?', ARRAY['quello indicato dal locatario sulla base del titolo amministrativo (licenza o autorizzazione', 'ad uso noleggio senza conducente', 'per locazione finanziaria senza conducente'], 'quello indicato dal locatario sulla base del titolo amministrativo (licenza o autorizzazione', 'Legislazione Siciliana'),
  ('In caso di acquisto rateale (vendita con patto di riservato dominio) di un veicolo da adibire a servizi pubblici non di linea, a nome di chi esso viene immatricolato?', ARRAY['a nome dell''acquirente', 'a nome del venditore', 'a nome dell''acquirente, ma con trascrizione sulla carta di circolazione del nome del venditore'], 'a nome dell''acquirente, ma con trascrizione sulla carta di circolazione del nome del venditore', 'Legislazione Siciliana'),
  ('In caso di trasferimento della licenza o dell''autorizzazione da parte di un titolare, quanto tempo deve trascorrere prima che egli possa riottenerne altra?', ARRAY['tre anni', 'non può riottenerne altra', 'cinque anni'], 'cinque anni', 'Legislazione Siciliana'),
  ('In che caso i veicoli da n.c.c. possono essere autorizzati allo stazionamento su suolo pubblico dal Comune, se qui non è esercito il servizio di taxi?', ARRAY['se stazionano su aree pubbliche destinate al servizio taxi', 'se stazionano su aree pubbliche in cui sia consentita la sosta', 'in nessun caso'], 'se stazionano su aree pubbliche destinate al servizio taxi', 'Legislazione Siciliana'),
  ('In cosa consiste un contratto di trasporto?', ARRAY['nell''obbligo da una parte del trasportatore di trasferire persone e cose da un luogo all''altro', 'nell''obbligo di mettere a disposizione di terzi un veicolo determinato con un conducente', 'nell''obbligo di guidare un veicolo per il tempo richiesto dal cliente'], 'nell''obbligo da una parte del trasportatore di trasferire persone e cose da un luogo all''altro', 'Legislazione Siciliana'),
  ('In quale dei seguenti casi le licenze e autorizzazioni possono essere trasferite a richiesta dal titolare?', ARRAY['se egli l''ha ottenuta da almeno tre anni', 'se egli ha raggiunto cinquantacinque anni', 'se egli è divenuto inabile o inidoneo al servizio in via permanente'], 'se egli è divenuto inabile o inidoneo al servizio in via permanente', 'Legislazione Siciliana'),
  ('In quali casi il servizio di noleggio con conducente è considerato trasporto pubblico urbano e a tale effetto è esente da IVA?', ARRAY['in nessun caso', 'per i soli trasportati interni al territorio comunale', 'per i soli trasportati interni al territorio comunale o tra comuni distanti tra loro al massimo 50 Km'], 'in nessun caso', 'Legislazione Siciliana'),
  ('In quali casi il servizio di taxi è considerato trasporto pubblico urbano e a tale effetto è esente da IVA?', ARRAY['in qualsiasi caso', 'per i soli trasportati interni al territorio comunale', 'per i soli trasportati interni al territorio comunale o tra comuni distanti tra loro al massimo 50 Km'], 'per i soli trasportati interni al territorio comunale o tra comuni distanti tra loro al massimo 50 Km', 'Legislazione Siciliana'),
  ('In quali comuni può essere consentito svolgere il servizio di taxi con autovetture immatricolate per servizio di noleggio con conducente?', ARRAY['quelli in cui il Comune ha liberamente regolamentato in tal modo', 'quelli di minore dimensione, individuati dalla Camera di Commercio su parere dell''Ufficio Periferico del Dipartimento dei Trasporti Terrestri – ex MCTC', 'nessun Comune'], 'quelli in cui il Comune ha liberamente regolamentato in tal modo', 'Legislazione Siciliana'),
  ('In quali comuni può esservi esonero dal dotare le autovetture addette al servizio taxi dell''apposito tassametro omologato?', ARRAY['quelli in cui il Comune ha liberamente regolamentato in tal modo', 'quelli di minore dimensione, individuati dalla Camera di Commercio su parere dell''Ufficio Periferico del Dipartimento dei Trasporti Terrestri – ex MCTC', 'nessun Comune'], 'quelli di minore dimensione, individuati dalla Camera di Commercio su parere dell''Ufficio Periferico del Dipartimento dei Trasporti Terrestri – ex MCTC', 'Legislazione Siciliana'),
  ('In quali dei seguenti casi le licenze e autorizzazioni possono essere trasferite a richiesta dal titolare?', ARRAY['se egli l''ha ottenuta da almeno cinque anni', 'se egli ha raggiunto cinquantacinque anni', 'se egli è divenuto inabile o inidoneo al servizio in via provvisoria'], 'se egli l''ha ottenuta da almeno cinque anni', 'Legislazione Siciliana'),
  ('La prestazione del servizio taxi ha obblighi particolari in relazione al territorio interessato dallo spostamento?', ARRAY['no', 'si, il trasporto non può essere rifiutato all''interno del Comune che ha rilasciato la licenza', 'si, il trasporto non può essere rifiutato in nessun caso'], 'si, il trasporto non può essere rifiutato all''interno del Comune che ha rilasciato la licenza', 'Legislazione Siciliana'),
  ('La prevista rimessa per autoservizio pubblico non di linea deve necessariamente essere situata nel territorio del Comune che rilascia il titolo amministrativo?', ARRAY['no, è sufficiente che nel territorio comunale sia presente una sede del titolare', 'no, è sufficiente che 1 o più rimesse siano situate sull''intero territorio regionale', 'no, può però essere situata al massimo in un comune limitrofo'], 'no, è sufficiente che 1 o più rimesse siano situate sull''intero territorio regionale', 'Legislazione Siciliana'),
  ('La sosta dei veicoli in servizio di n.c.c. presso stazioni ferroviarie, se consentita dal Comune, è possibile nelle stesse aree destinate al posteggio dei taxi?', ARRAY['si', 'no, le aree devono essere ben distinte da quelle dei taxi', 'no, le aree devono essere distanziate di almeno 300 metri'], 'no, le aree devono essere ben distinte da quelle dei taxi', 'Legislazione Siciliana'),
  ('La sostituzione alla guida di un titolare di licenza o autorizzazione all''esercizio di servizi pubblici non di linea deve essere autorizzata da qualche Ente?', ARRAY['si, deve essere autorizzata dal Comune che ha rilasciato la licenza o l''autorizzazione', 'no, è sufficiente una comunicazione all''Ufficio Periferico del Dipartimento dei Trasporti Terrestri – ex MCTC', 'no, è sufficiente tenere a bordo del veicolo copia del contratto di assunzione a tempo determinato'], 'si, deve essere autorizzata dal Comune che ha rilasciato la licenza o l''autorizzazione', 'Legislazione Siciliana'),
  ('Le autovetture addette al servizio taxi possono recare fasce colorate sulle fiancate?', ARRAY['no', 'solo una fascia colorata alta al massimo 6 cm, sotto il bordo inferiore del vano dei finestrini', 'si, purché sia dello stesso colore per ciascuna cooperativa di appartenenza'], 'solo una fascia colorata alta al massimo 6 cm, sotto il bordo inferiore del vano dei finestrini', 'Legislazione Siciliana'),
  ('Le licenze e autorizzazioni possono essere trasferite dal titolare, a richiesta, ad altra persona da esso designata?', ARRAY['solo se la persona possiede i requisiti necessari', 'solo se la persona designata possiede i requisiti necessari e in presenza di particolari situazioni di chi intende trasferire (età, anzianità di esercizio, salute)', 'solo in caso di cessione d''azienda, in qualsiasi momento'], 'solo se la persona designata possiede i requisiti necessari e in presenza di particolari situazioni di chi intende trasferire (età, anzianità di esercizio, salute)', 'Legislazione Siciliana'),
  ('Le persone giuridiche possono essere titolari di licenza o autorizzazione per autoservizi pubblici non di linea?', ARRAY['si, ma possono essere solo società cooperative tra tassisti e noleggiatori', 'no, i titolari possono essere solo persone fisiche iscritte a ruolo', 'si, possono essere anche società di capitali'], 'no, i titolari possono essere solo persone fisiche iscritte a ruolo', 'Legislazione Siciliana'),
  ('Le vetture addette al servizio taxi possono avere scritte e stemmi identificativi dell''azienda esercente e del Comune di appartenenza, posti sulle fiancate?', ARRAY['no, solo nella parte posteriore', 'si, purché previsto dal regolamento comunale', 'si, sono sempre ammessi'], 'si, sono sempre ammessi', 'Legislazione Siciliana'),
  ('L''esenzione da IVA per i servizi urbani svolti con veicoli da piazza è valida solo se il trasporto è pagato direttamente dal viaggiatore?', ARRAY['no, vale anche se il contratto di trasporto è stato stipulato da un terzo', 'si', 'il trasporto urbano con veicoli da piazza non è esente da IVA'], 'no, vale anche se il contratto di trasporto è stato stipulato da un terzo', 'Legislazione Siciliana'),
  ('L''esercente dei servizi di noleggio con conducente è obbligato a emettere fattura o a rilasciare ricevuta a fini fiscali per il servizio effettuato?', ARRAY['si, sempre', 'no, mai', 'solo se richiesto dal cliente'], 'si, sempre', 'Legislazione Siciliana'),
  ('L''esercente di servizi di taxi è obbligato a emettere fattura per il servizio effettuato?', ARRAY['si, sempre se richiesto dal cliente', 'no, ma deve rilasciare ricevuta ai fini non fiscali se richiesto', 'no, mai'], 'no, ma deve rilasciare ricevuta ai fini non fiscali se richiesto', 'Legislazione Siciliana'),
  ('Lo stazionamento dei taxi in attesa di prestare servizio deve avvenire:', ARRAY['nelle apposite rimesse', 'in qualunque piazza o via della citta''', 'nelle aree a loro destinate contraddistinte da apposita segnaletica orizzontale e verticale'], 'nelle aree a loro destinate contraddistinte da apposita segnaletica orizzontale e verticale', 'Legislazione Siciliana'),
  ('L''obbligo del colore uniforme per le autovetture adibite al servizio di taxi si estende a tutta la carrozzeria?', ARRAY['si', 'no, è sufficiente che siano di tale colore le porte e i cofani', 'no, possono esserne esclusi eventuali fregi accessori originali (ad esempio fasce laterali paraurti)'], 'no, possono esserne esclusi eventuali fregi accessori originali (ad esempio fasce laterali paraurti)', 'Legislazione Siciliana'),
  ('Nel servizio di noleggio con conducente con autovettura è possibile sostare in posteggio di stazionamento su suolo pubblico?', ARRAY['no, è sempre obbligatorio sostare presso la propria rimessa', 'si, si può liberamente circolare e sostare come i taxi', 'si, se nel comune non viene esercitato il servizio taxi'], 'si, se nel comune non viene esercitato il servizio taxi', 'Legislazione Siciliana'),
  ('Nel servizio di noleggio con conducente con autovettura si possono usare corsie preferenziali per taxi e autobus e altre facilitazioni previste per i servizi pubblici?', ARRAY['no', 'si', 'solo se espressamente indicato in appositi cartelli'], 'si', 'Legislazione Siciliana'),
  ('Nel servizio taxi, da dove deve avvenire la partenza del veicolo per il prelevamento dell''utente o per l''inizio del servizio?', ARRAY['sempre e soltanto dal territorio del Comune che ha rilasciato la licenza', 'può avvenire anche nel territorio di altri comuni appartenenti al comprensorio, se consentito', 'non esistono obblighi in proposito'], 'sempre e soltanto dal territorio del Comune che ha rilasciato la licenza', 'Legislazione Siciliana'),
  ('Nel servizio taxi, è necessario l''assenso del conducente per destinazioni esterne al territorio comunale o comprensoriale?', ARRAY['solo in servizio notturno', 'si, sempre', 'no, il servizio è obbligatorio se l''utente è già salito a bordo'], 'si, sempre', 'Legislazione Siciliana'),
  ('Nelle cooperative di trasporto – servizio taxi – costituite come cooperative di produzione e lavoro possono essere associati anche sostituti alla guida?', ARRAY['si', 'solo entro il limite percentuale determinato dal Comune con regolamento', 'no'], 'si', 'Legislazione Siciliana'),
  ('Nelle cooperative di trasporto – servizio taxi – costituite come cooperative di produzione e lavoro, che obblighi hanno i soci titolari di licenza?', ARRAY['il conferimento della licenza', 'il conferimento della licenza e dell''autovettura', 'il conferimento dell''autovettura'], 'il conferimento della licenza e dell''autovettura', 'Legislazione Siciliana'),
  ('Ogni quanto tempo deve essere verificato e adeguato il Piano Urbano del Traffico?', ARRAY['annualmente', 'ogni cinque anni', 'ogni due anni'], 'ogni due anni', 'Legislazione Siciliana'),
  ('Per esercitare il servizio di taxi è necessario essere titolare di un''impresa di trasporto?', ARRAY['no', 'si, purché sia una impresa artigiana', 'no, basta essere iscritti al registro dei mestieri girovaghi'], 'no', 'Legislazione Siciliana'),
  ('Per il rilascio di una licenza o autorizzazione per autoservizi pubblici non di linea, è utile avere esercitato attività di conducente come dipendente o sostituto?', ARRAY['si, costituisce titolo preferenziale se il servizio è stato prestato per almeno sei mesi', 'si, costituisce titolo preferenziale proporzionale al numero di anni di effettivo esercizio', 'no'], 'si, costituisce titolo preferenziale se il servizio è stato prestato per almeno sei mesi', 'Legislazione Siciliana'),
  ('Per le autovetture immatricolate per servizio taxi è sempre necessario esporre sul tetto il contrassegno luminoso con la scritta “taxi”?', ARRAY['no, possono esserne esentate nei comuni di minore dimensione', 'no, è facoltà a discrezione del conducente', 'si, è sempre obbligatorio'], 'si, è sempre obbligatorio', 'Legislazione Siciliana'),
  ('Per quali autoservizi pubblici non di linea è necessario disporre di una rimessa propria?', ARRAY['sia per il taxi che per il noleggio con conducente', 'solo per taxi', 'solo per noleggio con conducente'], 'solo per noleggio con conducente', 'Legislazione Siciliana'),
  ('Perché un contratto di lavoro sia realmente stipulato per un tempo determinato, in caso di sostituzione alla guida di taxi, è sufficiente che sia specificato il termine?', ARRAY['si', 'no, devono essere precisati anche il nome del tassista sostituito e la causa della sostituzione', 'no, deve essere specificata la targa del veicolo'], 'no, devono essere precisati anche il nome del tassista sostituito e la causa della sostituzione', 'Legislazione Siciliana'),
  ('Possono Comuni e Province differenziare i servizi di linea con autovetture ad uso terzi, non dotate di specifiche licenze o autorizzazioni per i servizi non di linea?', ARRAY['si, nel rispetto dei criteri tecnici e delle modalità di utilizzo disposti dal Ministero delle Infrastrutture e dei Trasporti', 'no, è possibile solo impiegare taxi e vetture da noleggio regolarmente dotate di licenze o autorizzazioni', 'si, solo se i veicoli sono di proprietà di titolari di licenza taxi o autorizzazioni al noleggio con conducente'], 'si, nel rispetto dei criteri tecnici e delle modalità di utilizzo disposti dal Ministero delle Infrastrutture e dei Trasporti', 'Legislazione Siciliana'),
  ('Possono essere conferite licenze o autorizzazioni per autoservizi pubblici non di linea a organismi associativi?', ARRAY['si, ma solo a società cooperative tra tassisti e noleggiatori', 'no', 'si, purché costituiti tra titolari di licenza o autorizzazioni'], 'si, purché costituiti tra titolari di licenza o autorizzazioni', 'Legislazione Siciliana'),
  ('Presso quale ente è istituito il ruolo dei conducenti di veicoli adibiti ad autoservizi pubblici non di linea?', ARRAY['la Camera di Commercio del capoluogo di Regione', 'la Camera di Commercio di ciascuna Provincia', 'la Provincia'], 'la Camera di Commercio di ciascuna Provincia', 'Legislazione Siciliana'),
  ('Quale aliquota IVA si applica ai servizi di noleggio con conducente?', ARRAY['0.10', '0,20', 'sono esenti'], '0.10', 'Legislazione Siciliana'),
  ('Quale autorità determina i criteri per il calcolo annuale delle tariffe per il servizio taxi?', ARRAY['il Comune', 'il Ministero delle Infrastrutture e dei Trasporti', 'la Provincia'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale autorità determina le modalità di turnazione per l''esercizio del servizio taxi?', ARRAY['il Comune', 'nessuna, la turnazione viene concordata direttamente tra gli operatori', 'la Regione'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale autorità stabilisce i limiti massimi delle licenze e delle autorizzazioni che possono essere assentite da ogni Comune?', ARRAY['la Camera di Commercio', 'la Provincia', 'la Regione'], 'la Regione', 'Legislazione Siciliana'),
  ('Quale contrassegno particolare devono esporre le autovetture adibite al servizio di noleggio con conducente?', ARRAY['un contrassegno luminoso, posto sul tetto, con la scritta NCC', 'un contrassegno con la scritta “noleggio” all''interno del parabrezza e del lunotto', 'la scritta “noleggio” dipinta sulla fiancata'], 'un contrassegno con la scritta “noleggio” all''interno del parabrezza e del lunotto', 'Legislazione Siciliana'),
  ('Quale dei seguenti non è definibile servizio pubblico non di linea ai sensi delle norme in vigore.', ARRAY['il noleggio di autovetture con conducente', 'il noleggio di motocarrozzette con conducente', 'la locazione di autovetture senza conducente'], 'la locazione di autovetture senza conducente', 'Legislazione Siciliana'),
  ('Quale è il minimo di garanzia per l''assicurazione obbligatoria della responsabilità civile per autovetture in servizio di taxi?', ARRAY['12.000.000,00 euro', '1.549.370,70 euro', '2.582.284,50 euro'], '12.000.000,00 euro', 'Legislazione Siciliana'),
  ('Quale è il presupposto perché un congiunto, lavorando in famiglia o impresa familiare, sia effettivamente considerato partecipante all''impresa familiare?', ARRAY['che sia assunto come dipendente', 'che presti la propria attività in modo continuativo', 'che abiti assieme al titolare dell''impresa'], 'che presti la propria attività in modo continuativo', 'Legislazione Siciliana'),
  ('Quale è l''autorità che approva in via definitiva i regolamenti comunali per l''esercizio dei servizi pubblici non di linea?', ARRAY['la Regione', 'la Provincia', 'il Comune'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale è l''autorità che consente il rinnovo e il trasferimento di titolarità delle licenze taxi e delle autorizzazioni per il servizio di noleggio con conducente?', ARRAY['la Regione', 'la Provincia', 'il Comune'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale è l''autorità che determina i requisiti per l''iscrizione al ruolo dei conducenti di veicoli in servizio pubblico non di linea?', ARRAY['la Regione', 'la Provincia', 'l''Ufficio Periferico del Dipartimento dei Trasporti Terrestri – ex MCTC'], 'la Regione', 'Legislazione Siciliana'),
  ('Quale è l''autorità che deve vigilare sulla regolamentazione comunale, affinché essa sia omogenea e garantisca la razionalità dei servizi?', ARRAY['la Regione', 'la Provincia', 'l''Ufficio Periferico del Dipartimento dei Trasporti Terrestri – ex MCTC'], 'la Regione', 'Legislazione Siciliana'),
  ('Quale è l''autorità che emana i regolamenti per i servizi pubblici non di linea?', ARRAY['la Regione', 'la Provincia', 'il Comune'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale è l''autorità che rilascia le autorizzazioni per il servizio di noleggio con conducente?', ARRAY['la Regione', 'la Provincia', 'il Comune'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale è l''autorità che rilascia le licenze per il servizio taxi?', ARRAY['la Regione', 'la Provincia', 'il Comune'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale è l''autorità competente per la disciplina generale dell''attività degli enti pubblici in materia di autoservizi pubblici non di linea?', ARRAY['la Provincia', 'il Ministero delle Infrastrutture e dei Trasport', 'la Regione'], 'la Regione', 'Legislazione Siciliana'),
  ('Quale è l''età minima per poter guidare autovetture adibite a servizio di piazza o di noleggio con conducente?', ARRAY['diciotto anni', 'ventuno anni', 'venticinque anni'], 'ventuno anni', 'Legislazione Siciliana'),
  ('Quale è lo scopo del servizio di taxi?', ARRAY['soddisfa esigenze solo di trasporto individuale', 'soddisfa esigenze solo di trasporto individuale o di piccoli gruppi', 'soddisfa esigenze solo di trasporto di qualsiasi genere, anche con contratto di noleggio'], 'soddisfa esigenze solo di trasporto individuale o di piccoli gruppi', 'Legislazione Siciliana'),
  ('Quale è l''unico requisito indispensabile, per legge statale, per ottenere l''iscrizione al ruolo dei conducenti di veicoli adibiti ad autoservizi pubblici non di linea?', ARRAY['la sola patente', 'la licenza o l''autorizzazione', 'il certificato di abilitazione professionale'], 'il certificato di abilitazione professionale', 'Legislazione Siciliana'),
  ('Quale Ente applica le sanzioni per l''inosservanza dell''obbligatorietà della prestazione del servizio di taxi?', ARRAY['la Regione', 'la Provincia', 'il Comune'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale ente determina le tariffe per il servizio di noleggio con conducente?', ARRAY['Il Comune sulla base dei criteri indicati dal Ministero delle Infrastrutture e dei Trasporti per la determinazione di una tariffa chilometrica minima e massima', 'nessuno, la tariffa varia tra un minimo e un massimo calcolati dal noleggiatore con criteri statali', 'la Provincia'], 'Il Comune sulla base dei criteri indicati dal Ministero delle Infrastrutture e dei Trasporti per la determinazione di una tariffa chilometrica minima e massima', 'Legislazione Siciliana'),
  ('Quale ente determina le tariffe per il servizio taxi?', ARRAY['il Comune', 'la Camera di Commercio', 'la Provincia'], 'il Comune', 'Legislazione Siciliana'),
  ('Quale organo comunale provvede a dare un parere sulle tariffe dei servizi di taxi ?', ARRAY['la Giunta', 'la Commissione Consultiva', 'la Commissione Tariffe'], 'la Commissione Consultiva', 'Legislazione Siciliana'),
  ('Quali tra gli eredi del titolare di licenza o autorizzazione deceduto ha il diritto al trasferimento del titolo amministrativo?', ARRAY['qualsiasi erede in linea diretta o indiretta', 'qualsiasi erede appartenente al nucleo familiare', 'ai soli figli maggiorenni'], 'qualsiasi erede appartenente al nucleo familiare', 'Legislazione Siciliana'),
  ('Quale responsabilità ha il vettore in caso di danno nei confronti delle persone trasportate causato da incidente?', ARRAY['totale, in ogni caso', 'totale, a meno che non provi di aver adottato tutte le misure per evitare il danno', 'totale, a meno che non abbia inserito nel contratto di trasporto specifiche clausole di esonero'], 'totale, a meno che non provi di aver adottato tutte le misure per evitare il danno', 'Legislazione Siciliana');
INSERT INTO questions (question, answers, correct_answer, category) VALUES
  ('A quale normativa si fa rinvio, nel Codice della Strada, per la disciplina del servizio di noleggio con conducente per trasporto di persone?', ARRAY['alla disciplina contenuta nel Codice e nel Regolamento stessi', 'alle leggi specifiche che regolano la materia', 'ai regolamenti comunali'], 'alle leggi specifiche che regolano la materia', 'Codice della Strada'),
  ('A seguito di un incidente stradale con feriti, cosa bisogna fare in attesa dell''arrivo dei soccorsi?', ARRAY['Cercare di ricostruire la dinamica dell''incidente', 'Rimuovere i veicoli incidentati', 'Proteggere il luogo dell''incidente e prestare prima assistenza ai feriti'], 'Proteggere il luogo dell''incidente e prestare prima assistenza ai feriti', 'Codice della Strada'),
  ('SI DEBBONO USARE GLI INDICATORI DI DIREZIONE', ARRAY['MAI, QUANDO SI MARCIA SU STRADA A SENSO UNICO', 'SOLO SE PER SVOLTARE BISOGNA ATTRAVERSARE LA CARREGGIATA', 'SE SI VIAGGIA SU STRADA SUDDIVISA IN CORSIE,PRIMA DI OGNI CAMBIO DI CORSIA'], 'SE SI VIAGGIA SU STRADA SUDDIVISA IN CORSIE,PRIMA DI OGNI CAMBIO DI CORSIA', 'Codice della Strada'),
  ('A seguito di un incidente stradale con feriti, se un infortunato è svenuto cosa è opportuno fare?', ARRAY['Controllare le sue funzioni vitali, battito cardiaco e respirazione', 'Farlo rinvenire dandogli da bere', 'Farlo sedere dove possibile'], 'Controllare le sue funzioni vitali, battito cardiaco e respirazione', 'Codice della Strada'),
  ('ALL''USCITA DELLE GALLERIE STRADALI E'' OPPORTUNO', ARRAY['NON SORPASSARE,NEANCHE NELLE CARREGGIATE A SENSO UNICO', 'RALLENTARE L''ANDATURA CON SUFFICIENTE ANTICIPO', 'FRENARE BRUSCAMENTE'], 'RALLENTARE L''ANDATURA CON SUFFICIENTE ANTICIPO', 'Codice della Strada'),
  ('All''uscita delle gallerie stradali è opportuno:', ARRAY['Frenare bruscamente', 'Non sorpassare, neanche nelle carreggiate a senso unico', 'All''uscita, tenere il volante con particolare attenzione per fronteggiare colpi di vento laterale'], 'All''uscita, tenere il volante con particolare attenzione per fronteggiare colpi di vento laterale', 'Codice della Strada'),
  ('Si deve chiamare la "POLIZIA" a seguito di incidente stradale:', ARRAY['Quando si è coinvolti in un incidente grave, indipendentemente dalle responsabilità', 'Quando non ci siano altri testimoni', 'Quando non si è sicuri di essere dalla parte della ragione'], 'Quando si è coinvolti in un incidente grave, indipendentemente dalle responsabilità', 'Codice della Strada'),
  ('Allorché ci accingiamo a scendere dall’autoveicolo dobbiamo:', ARRAY['Assicurarci, anche mediante lo specchietto retrovisore, che non sopraggiungano altri veicoli', 'Verificare, nella sosta davanti ad un passo carrabile, che rimanga spazio sufficiente per l’accesso', 'Lasciare sempre accese, di notte, le luci accese'], 'Assicurarci, anche mediante lo specchietto retrovisore, che non sopraggiungano altri veicoli', 'Codice della Strada'),
  ('ALLORCHE'' CI ACCINGIAMO A SCENDERE DALL''AUTOVEICOLO DOBBIAMO TRA L''ALTRO', ARRAY['PRIMA DI LASCIARE IL VEICOLO IN SOSTA,ASSICURARSI DI AVER SPENTO IL MOTORE', 'SPEGNERE IN OGNI CASO LE LUCI', 'PORRE IL CAMBIO IN POSIZIONE DI FOLLE'], 'PRIMA DI LASCIARE IL VEICOLO IN SOSTA,ASSICURARSI DI AVER SPENTO IL MOTORE', 'Codice della Strada'),
  ('Allorché, circolando di notte su strada extraurbana, veniamo abbagliati dai fari dell’autoveicolo che viene in senso opposto, occorre:', ARRAY['Procedere con prudenza, usando le luci anabbaglianti', 'Procedere a nostra volta con i fari abbaglianti, onde vedere bene la strada', 'Segnalare il pericolo azionando l’accensione simultanea di tutti gli indicatori di direzione'], 'Procedere con prudenza, usando le luci anabbaglianti', 'Codice della Strada'),
  ('CAUSA FREQUENTE DI INCIDENTI DOVUTI ALLE CONDIZIONI DELLA STRADA PUO'' ESSERE', ARRAY['PRESENZA DI OLIO SULL''ASFALTO', 'FONDO ASFALTATO RUVIDO', 'PRESENZA DI ISOLA DI TRAFFICO'], 'PRESENZA DI OLIO SULL''ASFALTO', 'Codice della Strada'),
  ('Causa frequente di incidenti dovuti alle condizioni della strada può essere:', ARRAY['Uso di pneumatici per neve su fondo asciutto', 'Inizio di pioggia su fondo asciutto', 'Fondo asfaltato ruvido'], 'Inizio di pioggia su fondo asciutto', 'Codice della Strada'),
  ('CAUSA PROBABILE DI INCIDENTI DOVUTI ALLA STRUTTURA DELLA STRADA PUO'' ESSERE', ARRAY['STRADA SUDDIVISA IN CARREGGIATE', 'CARREGGIATA A SENSO UNICO DI CIRCOLAZIONE', 'FONDO STRADALE DEFORMATO'], 'FONDO STRADALE DEFORMATO', 'Codice della Strada'),
  ('Che età minima deve avere un''autovettura per essere classificata di interesse storico o collezionistico?', ARRAY['vent''anni', 'trent''anni', 'cinquant''anni'], 'vent''anni', 'Codice della Strada'),
  ('SI DEVE CHIAMARE LA POLIZIA A SEGUITO DI INCIDENTE STRADALE', ARRAY['IN CASO DI INVESTIMENTO DI PERSONA CON LESIONI', 'QUANDO NON SI E'' SICURI DI ESSERE DALLA PARTE DELLA RAGIONE', 'QUANDO NON CI SIANO ALTRI TESTIMONI'], 'IN CASO DI INVESTIMENTO DI PERSONA CON LESIONI', 'Codice della Strada'),
  ('SI DEVE IN PARTICOLARE REGOLARE LA VELOCITA''', ARRAY['IN PROSSIMITA'' DELLE SCUOLE', 'FUORI DAI CENTRI ABITATI', 'QUANDO IL VEICOLO CHE SEGUE E'' DI CILINDRATA SUPERIORE'], 'IN PROSSIMITA'' DELLE SCUOLE', 'Codice della Strada'),
  ('SI DEVE TENERE UNA VELOCITA', ARRAY['PROPORZIONATA ALLA LUNGHEZZA DEL PERCORSO', 'ADEGUATA SOLO ALLE NOSTRE CONDIZIONI FISICHE', 'CHE NON SUPERI I LIMITI PRESCRITTI'], 'CHE NON SUPERI I LIMITI PRESCRITTI', 'Codice della Strada'),
  ('CHI GUIDA AUTOVEICOLI O MOTOCICLI DEVE ESIBIRE, A RICHIESTA DEGLI AGENTI, I SEGUENTI DOCUMENTI OVE PRESCRITTI:', ARRAY['LIBRETTO D''USO E MANUTENZIONE DEL VEICOLO', 'CONTRATTO DI LOCAZIONE CON FACOLTA'' DI COMPERA (LEASING)', 'PATENTE DI GUIDA'], 'PATENTE DI GUIDA', 'Codice della Strada'),
  ('CHI GUIDA UN VEICOLO, ANCHE SE AD ELEVATE PRESTAZIONI, DEVE COMUNQUE', ARRAY['GUARDARE NELLO SPECCHIETTO RETROVISORE PRIMA DI SEGNALARE L''INTENZIONE DI CAMBIARE CORSIA', 'SORPASSARE IN OGNI CONDIZIONE DI TRAFFICO,PURCHE'' NON CI SIA IL SEGNALE DI DIVIETO', 'TENERE IN FUNZIONE GLI ANABBAGLIANTI ANCHE DI GIORNO'], 'GUARDARE NELLO SPECCHIETTO RETROVISORE PRIMA DI SEGNALARE L''INTENZIONE DI CAMBIARE CORSIA', 'Codice della Strada'),
