-- ============================================
-- SETUP COMPLETO DATABASE
-- Quiz Ruolo Conducenti - 460 Domande
-- ============================================

-- 1. Crea la tabella
CREATE TABLE IF NOT EXISTS questions (
  id SERIAL PRIMARY KEY,
  question TEXT NOT NULL,
  answers TEXT[] NOT NULL,
  correct_answer TEXT NOT NULL,
  category TEXT,
  explanation TEXT,
  created_at TIMESTAMP DEFAULT NOW(),
  updated_at TIMESTAMP DEFAULT NOW()
);

-- 2. Abilita Row Level Security
ALTER TABLE questions ENABLE ROW LEVEL SECURITY;

-- 3. Policy per lettura pubblica
DROP POLICY IF EXISTS "Lettura pubblica delle domande" ON questions;
CREATE POLICY "Lettura pubblica delle domande"
ON questions FOR SELECT TO public USING (true);

-- 4. Indici per performance
CREATE INDEX IF NOT EXISTS idx_questions_category ON questions(category);
CREATE INDEX IF NOT EXISTS idx_questions_created_at ON questions(created_at DESC);

-- ============================================
-- INSERIMENTO DOMANDE (460 totali)
-- ============================================


-- TOPONOMASTICA PALERMO (40 domande)

-- TOPONOMASTICA SICILIA (39 domande)

-- LEGISLAZIONE SICILIANA (136 domande)

-- CODICE DELLA STRADA (245 domande)

-- ============================================
-- VERIFICA
-- ============================================
SELECT category, COUNT(*) as count 
FROM questions 
GROUP BY category 
ORDER BY category;

SELECT COUNT(*) as total_questions FROM questions;
